import React, { useState } from 'react'
import emailjs from 'emailjs-com'
import { init } from 'emailjs-com';
import 'bootstrap';
import 'react-bootstrap';
import './contact.css';


const Contact = () => {
    const [name, setName] = useState('');
    const [email, setEmail] = useState('');
    const [message, setMessage] = useState('');
    const [emailSent, setEmailSent] = useState(false);

    const submit = () => {
        if (name && email && message) {
            const serviceId = 'service_timok48';
            const templateId = 'template_lyo1xq8';
            const userId = 'OKPc3nssHifGDSPuv';
            const templateParams = {
                name,
                email,
                message
            };


            emailjs.send(serviceId, templateId, templateParams, userId)
                .then(response => console.log(response))
                .then(error => console.log(error));
    
            setName('');
            setEmail('');
            setMessage('');
            setEmailSent(true);
        } else {
            alert('Please fill in all fields.');
        }

        init('user_id');
    }
    return (
        <div>
            <div className='contact'>
              <div>
                <center><h3>Contact Us</h3></center>
                </div>
                  <div>
                      <input className='inputs' type="text" placeholder="Your Name" onChange={e => setName(e.target.value)} required/><br></br>
                      <input className='inputs' type="email" placeholder="Your email address" onChange={e => setEmail(e.target.value)} required/><br></br>
                      <textarea className='inputs' placeholder="Your message"  onChange={e => setMessage(e.target.value)} required></textarea><br></br>
                      <button className='btn-send' onClick={submit}>Send Message</button><br></br>
                      <span className={emailSent ? 'visible' : null}>Thank you for your message, we will be in touch in no time!</span>
                  </div>
            </div>
        </div>
    );
};

export default Contact;
